	freopen("tree.in", "r", stdin); 
#endif

	read(n); 
	for (int i = 1; i <= n; ++i) {
		read(d[i]); 
		sze[i] = 1; 
		pos[d[i]] = i; 
	}
	std::sort(d + 1, d + n + 1, std::greater<int>()); 

	for (int i = 1; i < n; ++i) {
		int v = pos[d[i]], u; 
		s64 faD = d[i] - n + 2 * sze[v]; 

		vis[v] = true; 
		if (!pos.count(faD) || vis[u = pos[faD]]) {
			invalid(); 
		}

		addEdge(u, v); 
	}

	if (check()) {
		for (int i = 0; i < n - 1; ++i) {
			printf("%d %d\n", ans[i].first, ans[i].second); 
		}
