const ll MOD = 1e9 + 7;

typedef decay<decltype(MOD)>::type T;
struct mi {
	T val;
	explicit operator T() const { return val; }
	mi() { val = 0; }
	mi(const ll& v) {
		val = (-MOD <= v && v <= MOD) ? v : v % MOD;
		if (val < 0) val += MOD;
	}
	// friend ostream& operator<<(ostream& os, const mi& a) {
		// return os << a.val; }
	friend void pr(const mi& a) { pr(a.val); }
	friend void re(mi& a) { ll x; re(x); a = mi(x); }

	friend bool operator==(const mi& a, const mi& b) {
		return a.val == b.val; }
	friend bool operator!=(const mi& a, const mi& b) {
		return !(a == b); }
	friend bool operator<(const mi& a, const mi& b) {
		return a.val < b.val; }

	mi operator-() const { return mi(-val); }
	mi& operator+=(const mi& m) {
		if ((val += m.val) >= MOD) val -= MOD;
		return *this; }
	mi& operator-=(const mi& m) {
		if ((val -= m.val) < 0) val += MOD;
		return *this; }
	mi& operator*=(const mi& m) {
		val = (ll)val*m.val%MOD; return *this; }
	friend mi pow(mi a, ll p) {
		mi ans = 1; assert(p >= 0);
		for (; p; p /= 2, a *= a) if (p&1) ans *= a;
		return ans;
	}
	friend mi inv(const mi& a) {
		assert(a != 0); return pow(a,MOD-2); }
	mi& operator/=(const mi& m) { return (*this) *= inv(m); }

	friend mi operator+(mi a, const mi& b) { return a += b; }
	friend mi operator-(mi a, const mi& b) { return a -= b; }
	friend mi operator*(mi a, const mi& b) { return a *= b; }
	friend mi operator/(mi a, const mi& b) { return a /= b; }
};

typedef pair<mi,mi> pmi;
typedef vector<mi> vmi;
typedef vector<pmi> vpmi;
const int MAX = 1e5 + 5;
const int INF = 1e9;
map<ll, int> conv;
vpi edges;
vi adj[MAX];
ll depth[MAX];
void dfs_depth(int src, int par){
    for(int nxt: adj[src]){
        if(nxt == par) continue;
        depth[nxt] = depth[src]+1;
        dfs_depth(nxt, src);
    }
}
int main(){
    int n; re(n);
    vl d(n);
    f0r(i, n) re(d[i]);
    set<pair<ll, int>, greater<pair<ll, int>>> s;
    f0r(i, n) s.insert(mp(d[i], 1));
    f0r(i, n) conv[d[i]] = i;
    while(sz(s)>1){
        auto ft = *s.begin();
        s.erase(ft);
        ll nxt = ft.f + 2*ft.s - n;
        auto it = s.lb(mp(nxt, INF));
        if(it == s.end()){
            ps(-1); return 0;
        }
        auto tmp = *it;
        if(tmp.f != nxt){
            ps(-1); return 0;
        }
        edges.eb(mp(conv[ft.f], conv[tmp.f]));
        s.erase(tmp);
        tmp.s += ft.s;
        s.insert(tmp);
    }
    int root = conv[(*s.begin()).f];
    ll val = (*s.begin()).f;
    for(auto e: edges) adj[e.f].eb(e.s), adj[e.s].eb(e.f);
    dfs_depth(root, -1);
    f0r(i, n) val -= depth[i];
    if(val != 0){
        ps(-1); return 0;
    }
    for(auto e: edges) ps(e.f+1, e.s+1);
    return 0;
}

