typedef long long ll;

int main() {
    std::map<ll, int, std::greater<ll> > M;
    std::ios::sync_with_stdio(0);
    int n;
    std::cin >> n;
    for(int i = 1; i <= n; ++i) {
        ll x;
        std::cin >> x;
        M.insert(std::make_pair(x, i));
    }
    static int fa[int(1e5) + 5];
    static int sz[int(1e5) + 5];
    std::set<int> Q;
    for(auto& k : M) if(k.first != (*M.rbegin()).first) {
        int now = k.second;
        ++sz[now];
        Q.insert(now);
        int f = k.first + 2 * sz[now] - n;
        if(M.count(f) == 0 || Q.count(M[f]) != 0) {
            puts("-1");
            return 0;
        }
        fa[now] = M[f];
        sz[fa[now]] += sz[now];
    }
    for(int i = 1; i <= n; ++i) if(fa[i] != 0) {
        std::cout << i << ' ' << fa[i] << '\n';
    }
    return 0;
}

