int N;
vector<ll> D;
int main(){
	cin>>N;
	D.resize(N);
	rep(i,N) cin>>D[i];
	using ll_i = pair<ll,int>;
	vector<ll_i> dis;
	map<ll,int> mp;
	rep(i,N){
		dis.pb(ll_i(D[i],i));
		mp[D[i]] = i;
	}
	sort(all(dis),greater<ll_i>());
	using P = pair<int,int>;
	vector<P> es;
	vector<int> sz(N);
	vector<ll> dsum(N);
	rep(_,N-1){
		int v = dis[_].sc;
		sz[v]++;
		ll tar = D[v] - (N-2*sz[v]);
		if(!mp.count(tar)){
			puts("-1");
			return 0;
		}
		int p = mp[tar];
		sz[p] += sz[v];
		dsum[p] += dsum[v] + sz[v];
		es.pb(P(v,p));
	}
	{
		int r = dis[N-1].sc;
		if(dsum[r] != D[r]){
			puts("-1");
			return 0;
		}
	}
	for(P p: es){
		cout<<p.fs+1<<" "<<p.sc+1<<endl;
	}
}

