int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    cin >> N;
    vector<long long> D(N);

    for (auto &d : D)
        cin >> d;

    vector<pair<long long, int>> sorted;

    for (int i = 0; i < N; i++)
        sorted.emplace_back(D[i], i);

    sort(sorted.begin(), sorted.end());
    vector<int> parent(N, -1);
    vector<int> subtree_size(N, 1);

    for (int i = N - 1; i > 0; i--) {
        int current = sorted[i].second;
        long long goal = sorted[i].first - (N - 2 * subtree_size[current]);
        int index = lower_bound(sorted.begin(), sorted.end(), make_pair(goal, 0)) - sorted.begin();
        int who = (index < i && sorted[index].first == goal) ? sorted[index].second : -1;

        if (who < 0) {
            cout << -1 << '\n';
            return 0;
        }

        subtree_size[who] += subtree_size[current];
        parent[current] = who;
    }

    for (int i = N - 1; i > 0; i--) {
        int current = sorted[i].second;
        cout << current + 1 << ' ' << parent[current] + 1 << '\n';
    }
}

