struct D{int w,x,s;}d[N];int n,he[N],to[N],nx[N];
bool cmp(D x,D y){return x.x>y.x;}
inline void wk(int w,int x,int y)
{to[w]=y,nx[w]=he[x],he[x]=w;}
int dfs(int x,int y)
{
	int z=y;
	for(int i=he[x];i;i=nx[i])
		z+=dfs(to[i],y+1);
	return z;
