	for(int i=1,x,l,r,I;i<n;++i)
	{
		for(x=0,l=i+1,r=n;l<=r;)
			I=l+r>>1,d[I].x>=d[i].x+2*d[i].s-n?(x=I,l=I+1):(r=I-1);
