			int x = H[k].top().Y;
			H[k].pop();
			addedge(x, y);
			size[x] = (n+d[y]-d[x])/2;
			size[y] -= size[x];
			Q.push(x);
		}
		if (size[y] != 1)
		{
			puts("-1");
			return 0;
		}
	}

	for (auto p : ans)
		printf("%d %d\n", p.X, p.Y);

	return 0;
}

