void bfs(int a) {
	for (int i = 1; i <= n; ++i) D[i] = n * 100;
	D[a] = 0;
	vector<int> q;
	q.push_back(a);
	for (int i = 0; i < (int)q.size(); ++i) {
		int v = q[i];
		for (int to : g[v]) {
			if (D[v] + 1 < D[to]) {
				D[to] = D[v] + 1;
				q.push_back(to);
			}
		}
	}
	for (int i = 1; i <= n; ++i) sumd += D[i];
	jfs(1);
}

void dfs(int v, int par = -1) {
	if (v != 1) {
		sumd -= S[v];
		sumd += (n - S[v]);
	}
	if (sumd != d[v]) OK = 0;
	for (int to : g[v]){
		if (to == par) continue;
		dfs(to, v);
	}
	if (v != 1) {
		sumd += S[v];
		sumd -= (n - S[v]);
	}
}

bool check() {
	bfs(1);
	dfs(1);
	return OK;
