		d.push_back(a);
		mp[a]=i;
	}
	for(auto it=mp.rbegin();it!=mp.rend();it++){
		int node=(*it).second;
		long long int val=(*it).first;
		sz[node]++;
		long long int op=n-sz[node];
		if(op==0)continue;
		long long int nex=val+sz[node]-op;
		if(mp.count(nex)){
			int ne=mp[nex];
			sz[ne]+=sz[node];
			v[ne].push_back(node);
			v[node].push_back(ne);
			if(ne>node)swap(ne,node);
			ed.push_back(make_pair(ne+1,node+1));
		}
	}
	dfs(0);
	dfs2(0);
	for(int i=0;i<n;i++){
		if(vis[i]==false){
			puts("-1");
			return 0;
		}
		if(ans[i]!=d[i]){
			puts("-1");
			return 0;
		}
	}
	for(int i=0;i<ed.size();i++){
