  rep(i, n) {
    cin >> vec.at(i);
    mp[vec.at(i)] = i+1;
  }

  vector<int> used(n);
  vector<int> graph[n+1];

  sort(vec.rbegin(), vec.rend());
  rep(i, n) {
    ll a = vec.at(i);
    int s = mp[a];
    if (used.at(s-1) == -1) continue;
