      ans.push_back(P(s,t));
      graph[s].push_back(t);
      graph[t].push_back(s);
      used.at(s-1) = -1;
    }
  }

  vector<int> distance(n);
  rep(i, n) {
    distance.at(i) = INT_MAX;
  }
  priority_queue<P, vector<P>, greater<P>> que;
