void dfs(int x, int fa)
{
	f[x] = 0;
	for (int i = 0; i < linker[x].size(); i ++)
	{
		int cur = linker[x][i];
		if (cur == fa) continue;
		dfs(cur, x), f[x] += f[cur] + sz[cur];
	}
}

void dfs2(int x, int fa)
{
	if (x == root) g[x] = 0;
	else g[x] = g[fa] + n - sz[x] + f[fa] - f[x] - sz[x];
	for (int i = 0; i < linker[x].size(); i ++)
	{
		int cur = linker[x][i];
		if (cur == fa) continue;
		dfs2(cur, x);
	}
}

inline bool check()
{
	dfs(root, 0), dfs2(root, 0);
//	for (int i = 1; i <= n; i ++)
//		printf("(%d, %d), ", f[i], g[i]);
//	puts("");
	for (int i = 1; i <= n; i ++)
		if (f[i] + g[i] != D[i]) return 0;
	return 1;
}

