const int maxn = 100050;

int n, sz[maxn];
pair<ll,int> d[maxn];
struct edge{int u, v;}e[maxn];

int main()
{
	// fp("1.in","r",stdin);
	// fp("1.out","w",stdout);
	sf("%d",&n);
	fo(i,1,n) sf("%I64d",&d[i].fi), d[i].se = i;
	sort(d+1, d+n+1);
	fd(i,n,2)
	{
		sz[i] ++;
		int l = 1, r = i-1, mid, p = -1;
		while(l <= r)
		{
			mid = (l + r) >> 1;
			if(d[mid].fi <= d[i].fi-n+2*sz[i]) p = mid, l = mid + 1;
			else r = mid - 1;
		}
		if(p == -1 || d[p].fi != d[i].fi-n+2*sz[i]) 
		{
			assert(0);
			return 0 * pf("-1\n");
		}
		sz[p] += sz[i];
		e[i].u = d[p].se; e[i].v = d[i].se;
	}
	sz[1] ++;
	ll ans = 0;
	fo(i,1,n) ans += sz[i] * (ll)(n - sz[i]);
	ans <<= 1;
	fo(i,1,n) ans -= d[i].fi;
	if(ans != 0) return 0 * pf("-1\n");
	fo(i,2,n) pf("%d %d\n",e[i].u,e[i].v);
	return 0;
}

