		int v = ord[i];
		int par = mp[D[v] - n + sz[v] + sz[v]];
		if (!par) kill(- 1);
		ans.push_back({v, par});
		adj[par].push_back(v);
		adj[v].push_back(par);
		sz[par] += sz[v];
	}
	DFS(1);
	if (cnt < n || sum != D[1]) kill(- 1);
	for (pii e : ans) cout << e.A << sep << e.B << endl;

    return 0;
}

