const int N = 100010;
int n, per[N], sz[N], cur;
ll dis[N];
map<ll,int> mp;
vector<pii> ans;
void fail() {
  puts("-1");
  exit(0);
}
struct edge {
  int la,b;
} con[N << 1];
int tot, fir[N];
void add_edge(int from,int to) {
  con[++tot] = (edge) {fir[from], to};
  fir[from] = tot;
}
ll sum[N], cal[N];
void dfs(int pos,int fa) {
  sum[pos] = cal[pos] = 0;
  for (int i = fir[pos]; i; i = con[i].la) {
    if (con[i].b == fa) continue;
    dfs(con[i].b, pos);
    sum[pos] += sum[con[i].b] + sz[con[i].b];
  }
}
void fsd(int pos,int fa) {
  cal[pos] += sum[pos];
  for (int i = fir[pos]; i; i = con[i].la) {
    if (con[i].b == fa) continue;
    cal[con[i].b] += cal[pos] - sum[con[i].b] - sz[con[i].b] + (n - sz[con[i].b]);
    fsd(con[i].b, pos);
  }
}
int main() {
  read(n);
  rep (i, 1, n) read(dis[i]);
  rep (i, 1, n) mp[dis[i]] = i;
  rep (i, 1, n) per[i] = i;
  sort(per + 1, per + n + 1, [=](int a,int b) -> bool {
      return dis[a] < dis[b];
    });
  rrp (i, n, 2) {
    ++ sz[per[i]];
    ll tmp = dis[per[i]] - n + 2 * sz[per[i]];
    if (!mp.count(tmp)) fail();
    sz[mp[tmp]] += sz[per[i]];
    ans.push_back(pii(mp[tmp], per[i]));
    add_edge(mp[tmp], per[i]);
  }
  dfs(per[1], 0);
  fsd(per[1], 0);
  rep (i, 1, n) if (cal[i] != dis[i])
    fail();
  rep (i, 0, (int)ans.size()-1) {
    printf("%d %d\n", ans[i].fir, ans[i].sec);
  }
  return 0;
}

