	N = getnum ();
	for (int i = 1; i <= N; i ++) d[i].first = getnum (), d[i].second = i;
	sort (d + 1, d + N + 1);
	solve ();
	for (int i = 2; i <= N; i ++) G[fa[d[i].second]].push_back(d[i].second);
	DFS (d[1].second);
	if (total != d[1].first) { puts ("-1"); return 0; }
	for (int i = 2; i <= N; i ++)
		printf ("%d %d\n", fa[d[i].second], d[i].second);

	return 0;
}

