typedef long long LL;
typedef pair<int, int> pii;

const int MAXN = (int) 1e5;

int n;

struct node
{
	int u;
	LL D;

	inline bool operator < (const node &rhs) const { return this->D < rhs.D; }

	node() { }
	node(int _u, LL _D): u(_u), D(_D) { }
}d[MAXN + 5];

LL D[MAXN + 5];

inline void input()
{
	n = read<int>();
	for(int i = 1; i <= n; ++i) d[i] = node(i, D[i] = read<LL>());
}

int size[MAXN + 5];
vector<int> adj[MAXN + 5];

inline LL dfs_sum(int u)
{
	LL res = size[u] - 1;
	for(auto v : adj[u]) res += dfs_sum(v);
	return res;
}

inline int dfs_chk(int u, LL D0)
{
	if(D[u] != D0) return 0;
	for(auto v : adj[u]) if(!dfs_chk(v, D0 - size[v] + n - size[v])) return 0;
	return 1;
}

inline void solve()
{
	static priority_queue<node> Q;
	static unordered_map<LL, int> id;
	static int vis[MAXN + 5];

	for(int i = 1; i <= n; ++i)
	{
		Q.push(d[i]);
		id[d[i].D] = d[i].u;
		size[d[i].u] = 1;
	}

	static vector<pii> E;

	while(SZ(Q) > 1)
	{
		int u = Q.top().u;
		LL D0 = Q.top().D;
		vis[u] = 1, Q.pop();

		int v = id[D0 + 2 * size[u] - n];
		if(!v || vis[v]) { puts("-1"); return; }
		E.emplace_back(u, v);
		adj[v].push_back(u);
		size[v] += size[u];
	}

	int rt = Q.top().u;
	if(!dfs_chk(rt, dfs_sum(rt))) { puts("-1"); return; }

	for(auto i : E) printf("%d %d\n", i.fst, i.snd);
}

