					answ.push_back(make_pair(v, odd[it1].second));
					++it1;
				}
				if (sum != 0) {
					cout << -1 << endl;
					return 0;
				}
			}
			else {
				long long sum = order[v];
				while (sum > 0 && it2 < (long long)even.size()) {
					sum -= 1 + (n - 2 - (even[it2].first - d)) / 2;
					long long to = even[it2].second;
					order[to] = (n - 2 - (even[it2].first - d)) / 2;
					if (order[to] != 0) {
						q.push(even[it2]);
					}
					answ.push_back(make_pair(v, even[it2].second));
					++it2;
				}
				if (sum != 0) {
					cout << -1 << endl;
					return 0;
				}
			}
		}
	}
	for (int i = 0; i < (int)answ.size(); i++) {
		cout << answ[i].first << " " << answ[i].second << endl;
	}
	return 0;
}

