const int MN = 1e5 + 5;

pii d[MN];

std::map<ll, int> mp;

int n, fa[MN], sz[MN], rt;

ll dp[MN];

struct Edge {
	int to;
	Edge *nxt;
	Edge(Edge *nxt = nullptr, int to = 0) : to(to), nxt(nxt){}
}*hd[MN];

inline void ins(int u, int v) {hd[u] = new Edge(hd[u], v);}

void dfs(int u) {
	for (R Edge *e = hd[u]; e != nullptr; e = e -> nxt) {
		R int v = e -> to;
		dfs(v);
		dp[u] += sz[v] + dp[v];
	}
}

void solve(int u) {
	for (R Edge *e = hd[u]; e != nullptr; e = e -> nxt) {
		R int v = e -> to;
		dp[v] += (n - sz[v]) + (dp[u] - dp[v] - sz[v]);
		solve(v);
	}
}

