template<typename T, const T INFT>
struct Dijkstra{
  typedef pair<int,T> pit;
  typedef pair<T,int> pti;  
  vector<vector<pit>> G;
  vector<T> dist;
  int n;
  void init(int arg_n){
    n = arg_n;
    G.assign(n, vector<pit>(0));
  }
  void add_biedge(int fr, int to, T co){
    G[fr].pb(to, co);
    G[to].pb(fr, co);
  }
  void add_edge(int fr, int to, T co){
    G[fr].pb(to, co);
  }
  T dijkstra(int s, int t = -1){
    dist = vector<T>(n, INFT);
    dist[s] = 0;
    priority_queue<pti,vector<pti>,greater<pti>> que;
    que.emplace(0, s);
    while(!que.empty()){
      T d = que.top().fi;
      int p = que.top().se;
      que.pop();
      if(d > dist[p]){
	continue;
      }
      if(p == t){
	return d;
      }
      YYS(w, G[p]){
	int to = w.fi;
	T co = w.se;
	if(d + co < dist[to]){
	  dist[to] = d + co;
	  que.emplace(dist[to], to);
	}
      }
    }
    return INFT;
  }
};

Dijkstra<ll, INFLL> dij;

using pl = pair<ll, ll>;
vector<ll> G[100010];

int main(){

  ll n = in();
  vector<ll> a(n);
  REP(i, n){
    a[i] = in();
  }
  vector<ll> s(n, 1);
  vector<pl> q(0);
  REP(i, n){
    q.pb(a[i], i);
  }
  sort(q.begin(), q.end());
  reverse(q.begin(), q.end());
  map<ll, vector<pl>> call;
  dij.init(n);
  YYS(w, q){
    ll i = w.se;
    if(call.find(a[i]) != call.end()){
      YYS(c, call[a[i]]){
        // cout << "M " << i << " " << c.fi << endl;
        G[i].pb(c.fi);
        dij.add_biedge(i, c.fi, 1);
        s[i] += c.se;
      }
    }
    ll target = a[i] + s[i] - (n - s[i]);
    // cout << "T " << i << " " << target << endl;
    if(call.find(target) == call.end()){
      call[target] = vector<pl>(0);
    }
    call[target].pb(i, s[i]);
  }

  bool ok = false;
  ll pos = -1;
  REP(i, n){
    if(s[i] == n){
      ok = true;
      pos = i;
    }
  }

  if(pos != -1){
    dij.dijkstra(pos);

    ll s = 0;
    REP(i, n){
      s += dij.dist[i];
    }

    if(s != a[pos]){
      ok = false;
    }
  }

  if(ok){
    ll cnt = 0;
    REP(i, n){
      YYS(w, G[i]){
        cnt++;
        printf("%lld %lld\n", i+1, w+1);
      }
    }
    assert(cnt == n-1);
  } else {
    puts("-1");
  }

  return 0;
}

