const int N = 1e5;
vector<vector<int>> g(N);
using Value = ll;
map<int, Value> dp[N];
map<int, int> sz[N];
int n;
Value dfs(int i, int p, int f = 0) {
  if(dp[i].count(p)) return dp[i][p];
  int cnt = 0;
  Value res = 0;
  if(f || p == -1) {
    // O(deg(i))
    // go only child
    sz[i][p] = 1;
    for(int j : g[i]) if(j != p) {
      cnt++;
      res += dfs(j, i, f);
      res += sz[j][i];
      sz[i][p] += sz[j][i];
    }
  } else {
    // O(1)
    dfs(i, -1, f);
    dfs(p, i, f);
    res = dp[i][-1] - (dp[p][i] + sz[p][i]);
    cnt = g[i].size() - 1;
    sz[i][p] = sz[i][-1] - sz[p][i];
  }
  return dp[i][p] = res;
}

