using namespace std;

typedef long long ll;

const int MAXN = 100005;

struct Edge
{
	int to;
	int next;
}edge[MAXN << 1];

int n,id;
int first[MAXN];
int cnt[MAXN];
int ord[MAXN];
int f[MAXN];
int g[MAXN];

ll d[MAXN];
ll F[MAXN];
ll G[MAXN];

vector<pair<int,int> > e;

bool cmp(const int &a,const int &b)
{
	return d[a] < d[b];
}

void addE(int u,int v)
{
	edge[++id] = (Edge){v,first[u]};
	first[u] = id;
}

int search(ll v)
{
	int low = 1,high = n;
	while (low < high)
	{
		int mid = (low + high) >> 1;
		if (d[ord[mid]] >= v)
			high = mid;
		else
			low = mid + 1;
	}
	if (d[ord[low]] == v)
		return low;
	return -1;
}

void dfs1(int now,int fa)
{
	f[now] = 1;
	F[now] = 0;
	for (int i = first[now];i;i = edge[i].next)
		if (edge[i].to != fa)
		{
			dfs1(edge[i].to,now);
			F[now] += F[edge[i].to] + f[edge[i].to];
			f[now] += f[edge[i].to];
		}
}

void dfs2(int now,int fa)
{
	for (int i = first[now];i;i = edge[i].next)
		if (edge[i].to != fa)
		{
			G[edge[i].to] = G[now] + F[now] - F[edge[i].to] - f[edge[i].to] + g[now] + f[now] - f[edge[i].to];
			g[edge[i].to] = g[now] + f[now] - f[edge[i].to];
			dfs2(edge[i].to,now);
		}
}

int main()
{
	ios::sync_with_stdio(false);
	cin >> n;
	for (int i = 1;i <= n;i++)
	{
		cin >> d[i];
		cnt[i] = 1;
		ord[i] = i;
	}
	sort(ord + 1,ord + n + 1,cmp);
	for (int i = n;i >= 2;i--)
	{
		int pos = search(d[ord[i]] - (n - cnt[ord[i]]) + cnt[ord[i]]);
