using pi = pair<lint, int>;
const int MAXN = 100005;

void my_ass(bool p){
	if(!p){
		puts("-1");
		exit(0);
	}
}

int n;
pi a[MAXN];
vector<int> gph[MAXN];
set<int> s[2];
int left_size[MAXN];

lint get_dist(int x, int p, int d){
	lint ret = d;
	for(auto &i : gph[x]){
		if(i != p){
			ret += get_dist(i, x, d + 1);
		}
	}
	return ret;
}

vector<pi> new_event[MAXN];

void INSERT(int pos, int val, int atleast){
	lint v = n + a[val].second - 2 * left_size[a[val].second] + 2;
	int l = lower_bound(a + 1, a + n + 1, pi(v, -1)) - a;
	new_event[max(l, atleast)].push_back(pi(pos, val));
}

int main(){
	scanf("%d",&n);
	for(int i=1; i<=n; i++){
		scanf("%lld",&a[i].first);
		a[i].second = i;
	}
	sort(a + 1, a + n + 1);
	left_size[a[1].second] = n;
	INSERT(a[1].first % 2, 1, 2);
	for(int i=2; i<=n; i++){
		for(auto &j : new_event[i]){
			s[j.first].insert(j.second);
		}
		int msk = (a[i].first % 2) ^ (n % 2);
		my_ass(!s[msk].empty());
		my_ass(a[i].first <= n + a[*s[msk].begin()].first - 2);	
		int pidx = *s[msk].begin();
		/*
		for(auto &j : s[msk]){
			if(n + a[j].first - 2 * left_size[a[j].second] + 2 <= a[i].first){
				pidx = j;
				break;
			}
		}*/
		my_ass(pidx != -1);
		int par = a[pidx].second;
		int cur = a[i].second;
		gph[par].push_back(cur);
		gph[cur].push_back(par);
		left_size[cur] = n - (a[i].first - a[pidx].first);
		left_size[cur] /= 2;
		left_size[par] -= left_size[cur];
		s[msk].erase(pidx);
		if(left_size[par] != 1){
			INSERT(msk, pidx, i + 1);
		}
		if(left_size[cur] != 1){
			INSERT(a[i].first % 2, i, i + 1);
		}
	}
	for(int i=1; i<=1; i++){
		my_ass(get_dist(a[i].second, -1, 0) == a[i].first); 
	}
	for(int i=1; i<=n; i++){
		for(auto &j : gph[i]){
			if(i < j) printf("%d %d\n", i, j);
		}
	}
}

