    vector<int> subtree_size(N, 1);

    for (int i = N - 1; i >= 0; i--) {
        int current = sorted[i].second;
        int goal = sorted[i].first - (N - 2 * subtree_size[current]);
        int index = lower_bound(sorted.begin(), sorted.end(), make_pair(goal, 0)) - sorted.begin();
        int who = sorted[index].first == goal ? sorted[index].second : -1;

        if (i > 0 && (index >= i || who < 0)) {
            cout << -1 << '\n';
            return 0;
        }

        if (who >= 0)
            subtree_size[who] += subtree_size[current];

        parent[current] = who;
    }

    for (int i = N - 1; i > 0; i--) {
        int current = sorted[i].second;
        cout << current + 1 << ' ' << parent[current] + 1 << '\n';
    }
}

