inline int read()
{
	int sum=0,p=1;char ch=getchar();
	while(!(('0'<=ch && ch<='9') || ch=='-'))ch=getchar();
	if(ch=='-')p=-1,ch=getchar();
	while('0'<=ch && ch<='9')sum=sum*10+ch-48,ch=getchar();
	return sum*p;
}

const int maxn=1e5+20;
const ll inf=1ll<<60;

int n;
ll D[maxn];
int p[maxn];
int sa[maxn],sz[maxn];

map<ll,int>mp;

inline void init()
{
	n=read();
	REP(i,1,n)cin>>D[i],mp[D[i]]=i,sz[i]=1;
	D[0]=inf;
}

inline bool cmp(int a,int b){ return D[a]<D[b];} 

struct node {
	int v,next;
};
node e[maxn<<1];
int cnt,start[maxn];
inline void addedge(int u,int v){ e[++cnt]=(node){v,start[u]};start[u]=cnt;}

ll dist[maxn];

void dfs(int u,int fa)
{
	sz[u]=1;
	EREP(i,u)
	{
		int v=e[i].v;
		if(v==fa)continue;
		dfs(v,u);
		sz[u]+=sz[v];
		dist[u]+=dist[v]+sz[v];

	}
}

int l[maxn],r[maxn],tot;

inline void doing()
{
	REP(i,1,n)p[i]=i;
	sort(p+1,p+n+1,cmp);
	REP(i,1,n)sa[p[i]]=i;
	DREP(i,n,2)
	{
		int u=p[i];
		int x=mp[D[u]-n+2*sz[u]];
		if(!x)puts("-1"),exit(0);
		sz[x]+=sz[u];
		++tot;l[tot]=x;r[tot]=u;//printf("%d %d\n",x,u);
		addedge(x,u);
		addedge(u,x);
	}
	dfs(1,0);
	if(dist[1]!=D[1])puts("-1"),exit(0);
	REP(i,1,tot)printf("%d %d\n",l[i],r[i]);
}

int main()
{
	init();
	doing();
	return 0;
}

