	for(auto &T : adj[I]) {
		dfs1(T);
		rd[I] += rd[T] + cnt[T];
	}
}

void dfs2 (ll I) {
	for(auto &T : adj[I]) {
		rd[T] = rd[I] - cnt[T] + (n-cnt[T]);
		dfs2(T);
	}
