int main()
{
    int N;
    std::cin >> N;
    std::map<ll, int> mp, mp2;
    std::vector<ll> D(N);
    for (int i = 0; i < N; i++) { std::cin >> D[i], mp[D[i]] = i; }
    std::sort(D.begin(), D.end(), std::greater<ll>{});
    for (int i = 0; i < N; i++) { mp2[D[i]] = i; }
    std::vector<std::vector<int>> edge(N);
    std::vector<int> sub(N, 1);
    for (int i = 0; i < N - 1; i++) {
        const ll obj = D[i] - (N - 2 * sub[i]);
        if (obj == D[i] or mp2.find(obj) == mp2.end()) { return std::cout << -1 << std::endl, 0; }
        const int prev = mp2[obj];
        edge[mp[obj]].push_back(mp[D[i]]), sub[prev] += sub[i];
    }
    ll sum = 0;
    auto check = [&](auto&& self, const int s, const int d) -> void {
        sum += d;
        for (const int to : edge[s]) { self(self, to, d + 1); }
    };
    check(check, mp[D[N - 1]], 0);
    if (D[N - 1] == sum) {
        for (int i = 0; i < N; i++) {
            for (const int to : edge[i]) {
                std::cout << i + 1 << " " << to + 1 << std::endl;
            }
        }
    } else {
        std::cout << -1 << std::endl;
    }
    return 0;
}

