using namespace std;

int N;
long long D[100010];
int cnt[100010];
int ansu[100010], ansv[100010];

int bsrch(int ll, int rr, long long vv)
{
	int l=ll, r=rr;
	int m;
	while(l<r){
		m = (l+r)/2;
		if(D[m] >= vv){
			r = m;
		}
		else
			l = m+1;
	}
	return l;
}

int solve()
{
	int i, j=0;
	sort(D, D+N);
	for(i=N-1; i>0; i--){
		int p = bsrch(0, i, D[i]-(N-2-cnt[i]*2) );
		if(p>=i || D[p]!=D[i]-(N-2-cnt[i]*2) )
			return 0;
		cnt[p] += cnt[i]+1;
		ansu[j] = p;
		ansv[j] = i;
		j ++;
	}
	for(i=0; i<N-1; i++){
		printf("%d %d\n", ansu[i]+1, ansv[i]+1);
	}
	return 1;
}

