#include <stdio.h>
#include <stdlib.h>

#define N	100000

long long ss[N];

int compare(const void *a, const void *b) {
	int i = *(int *) a;
	int j = *(int *) b;

	return ss[i] < ss[j] ? -1 : 1;
}

int search(int *ii, int n, long long s) {
	int lower = -1, upper = n;

	while (upper - lower > 1) {
		int h = (lower + upper) / 2;

		if (ss[ii[h]] <= s)
			lower = h;
		else
			upper = h;
	}
	return lower == -1 || ss[ii[lower]] != s ? -1 : ii[lower];
}

int main() {
	static int ii[N], sz[N], pp[N], dd[N];
	int n, i;
	long long sum;

	scanf("%d", &n);
	for (i = 0; i < n; i++) {
		scanf("%lld", &ss[i]);
		ii[i] = i;
	}
	qsort(ii, n, sizeof *ii, compare);
	for (i = 0; i < n; i++)
		sz[i] = 1;
	for (i = n - 1; i > 0; i--) {
		int p = search(ii, n, ss[ii[i]] - (n - sz[ii[i]] * 2));

		if (sz[ii[i]] * 2 >= n || p == -1) {
			printf("-1\n");
			return 0;
		}
		pp[ii[i]] = p, sz[p] += sz[ii[i]];
	}
	sum = 0;
	for (i = 1; i < n; i++)
		sum += dd[ii[i]] = dd[pp[ii[i]]] + 1;
	if (ss[ii[0]] != sum) {
		printf("-1\n");
		return 0;
	}
	for (i = 1; i < n; i++)
		printf("%d %d\n", ii[i] + 1, pp[ii[i]] + 1);
	return 0;
}

