public class Main {
    public static void main(String[] args) {
        StringBuilder out = new StringBuilder();
        solve(out);
        PrintWriter pw = new PrintWriter(System.out);
        pw.println(out);
        pw.flush();
        pw.close();
    }

    public static void solve(StringBuilder out) {
        int n = In.ni();
        long[] d = In.nl(n);
        int[] idx = IntArrayGenerator.indexToInt(n, i -> i);
        DependentSort.sortDescending(d, idx);
        HashMap<Long, Integer> map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            map.put(d[i], i);
        }
        IntArrayList[] ch = new IntArrayList[n];
        for (int i = 0; i < n; i++) {
            ch[i] = new IntArrayList();
        }
        for (int v = 0; v < n - 1; v++) {
            long s = 0;
            for (int i : ch[v]) {
                s += (long) n - d[i] + d[v];
            }
            long du = s + d[v] - (n - 2);
            if (du >= d[v]) {
                out.append(-1);
                return;
            } else if (map.containsKey(du)) {
                ch[map.get(du)].add(v);
            } else {
                out.append(-1);
                return;
            }
        }
        long s = 0;
        int[] dep = new int[n];
        Arrays.fill(dep, 0, n - 1, -1);
        IntQueue q = new IntQueue(n);
        q.add(n - 1);
        while (q.size() > 0) {
            int u = q.poll();
            s += dep[u];
            for (int v : ch[u]) {
                if (dep[v] < 0) {
                    dep[v] = dep[u] + 1;
                    q.add(v);
                }
            }
        }
        if (s != d[n - 1]) {
            out.append(-1);
            return;
        }
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = idx[i];
        }
        for (int u = 0; u < n; u++) {
            for (int v : ch[u]) {
                out.append(p[u] + 1).append(' ').append(p[v] + 1).append('\n');
            }
        }
    }
}

final class InsertionSort {
    protected static void sort(final int[] a) {
        sort(a, 0, a.length);
    }

    protected static void sort(final int[] a, final int from, final int to) {
        for (int i = from + 1; i < to; i++) {
            final int tmp = a[i];
            if (a[i - 1] > tmp) {
                int j = i;
                do {
                    a[j] = a[j - 1];
                    j--;
                } while (j > from && a[j - 1] > tmp);
                a[j] = tmp;
            }
        }
    }

    protected static void sort(final long[] a) {
        sort(a, 0, a.length);
    }

    protected static void sort(final long[] a, final int from, final int to) {
        for (int i = from + 1; i < to; i++) {
            final long tmp = a[i];
            if (a[i - 1] > tmp) {
                int j = i;
                do {
                    a[j] = a[j - 1];
                    j--;
                } while (j > from && a[j - 1] > tmp);
                a[j] = tmp;
            }
        }
    }
}



