                out.println(String.format("%d %d", e[0]+1, e[1]+1));
            }
        } else {
            out.println(-1);
        }
        out.flush();
    }

    static boolean[] visited;
    static List<Integer>[] graph;
    static int[] child;

    static long dfs2(int r) {
        long total = 0;
        for (int to : graph[r]) {
            total += dfs2(to) + child[to];
        }
        return total;
    }

    static boolean dfs(int r) {
        if (visited[r]) {
            return false;
        }
        visited[r] = true;
        boolean ok = true;
        for (int to : graph[r]) {
            ok &= dfs(to);
        }
        return ok;
    }

