import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Comparator;
import java.util.InputMismatchException;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni();
		long[][] di = new long[n][];
		for(int i = 0;i < n;i++){
			di[i] = new long[]{nl(), i};
		}

		Arrays.sort(di, new Comparator<long[]>() {
			public int compare(long[] a, long[] b) {
				return Long.compare(a[0], b[0]);
			}
		});
		int[] des = new int[n];
		long[] vs = new long[n];
		for(int i = 0;i < n;i++){
			vs[i] = di[i][0];
		}
		Arrays.fill(des, 1);
		int[] from = new int[n-1];
		int[] to = new int[n-1];

		int p = 0;
		boolean[] done = new boolean[n];
		for(int i = n-1;i >= 0;i--){
			long val = di[i][0];
			int cur = (int)di[i][1];

			// (n-d)/2 = des
			// n-des*2 = d
			if(n-des[cur]*2 > 0){
				long d = n-des[cur]*2;
				int ind = lowerBound(vs, val - d);
				if(ind >= n){
					out.println(-1);
					return;
				}
				if(vs[ind] != val - d){
					out.println(-1);
					return;
				}
				des[(int)di[ind][1]] += des[cur];
				done[cur] = true;
				from[p] = cur;
				to[p] = (int)di[ind][1];
				p++;
			}
		}

		int[] ds = new int[2];
		int q = 0;
		for(int i = 0;i < n;i++){
			if(!done[i]){
				if(q == 1){
					out.println(-1);
					return;
				}
				ds[q++] = i;
			}
		}
		if(q == 2){
			from[p] = ds[0];
			to[p] = ds[1];
			p++;
		}

		assert p == n-1;
		int[][] g = packU(n, from, to);

		Arrays.sort(di, new Comparator<long[]>() {
			public int compare(long[] a, long[] b) {
				return Long.compare(a[1], b[1]);
			}
		});

		for(int k = 0;k < 2;k++){
			int[][] pars = parents3(g, ds[k]);
			long dsum = 0;
			for(int i = 0;i < n;i++){
				dsum += pars[2][i];
			}
			if(dsum != di[ds[k]][0]){
				out.println(-1);
				return;
			}
		}

		for(int i = 0;i < n-1;i++){
			out.println((from[i]+1) + " " + (to[i] + 1));
		}
	}

	static int[][] packU(int n, int[] from, int[] to) {
		int[][] g = new int[n][];
		int[] p = new int[n];
		for (int f : from)
			p[f]++;
		for (int t : to)
			p[t]++;
		for (int i = 0; i < n; i++)
			g[i] = new int[p[i]];
		for (int i = 0; i < from.length; i++) {
			g[from[i]][--p[from[i]]] = to[i];
			g[to[i]][--p[to[i]]] = from[i];
		}
		return g;
	}

	public static int[][] parents3(int[][] g, int root) {
		int n = g.length;
		int[] par = new int[n];
		Arrays.fill(par, -1);

		int[] depth = new int[n];
		depth[0] = 0;

		int[] q = new int[n];
		q[0] = root;
		for (int p = 0, r = 1; p < r; p++) {
			int cur = q[p];
			for (int nex : g[cur]) {
				if (par[cur] != nex) {
					q[r++] = nex;
					par[nex] = cur;
					depth[nex] = depth[cur] + 1;
				}
			}
		}
		return new int[][] { par, q, depth };
	}


	public static int lowerBound(long[] a, long v)
	{
		int low = -1, high = a.length;
		while(high-low > 1){
			int h = high+low>>>1;
			if(a[h] >= v){
				high = h;
			}else{
				low = h;
			}
		}
		return high;
	}


	public static void main(String[] args) throws Exception
	{
		long S = System.currentTimeMillis();
		is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		solve();
		out.flush();
		long G = System.currentTimeMillis();
		tr(G-S+"ms");
	}

	private static boolean eof()
	{
		if(lenbuf == -1)return true;
		int lptr = ptrbuf;
		while(lptr < lenbuf)if(!isSpaceChar(inbuf[lptr++]))return false;

		try {
			is.mark(1000);
			while(true){
				int b = is.read();
				if(b == -1){
					is.reset();
					return true;
				}else if(!isSpaceChar(b)){
					is.reset();
					return false;
				}
			}
		} catch (IOException e) {
			return true;
		}
	}

	private static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	private static int readByte()
	{
		if(lenbuf == -1)throw new InputMismatchException();
		if(ptrbuf >= lenbuf){
			ptrbuf = 0;
			try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
			if(lenbuf <= 0)return -1;
		}
		return inbuf[ptrbuf++];
	}

	private static boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
//	private static boolean isSpaceChar(int c) { return !(c >= 32 && c <= 126); }
	private static int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

	private static double nd() { return Double.parseDouble(ns()); }
	private static char nc() { return (char)skip(); }

	private static String ns()
	{
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while(!(isSpaceChar(b))){
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	private static char[] ns(int n)
	{
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while(p < n && !(isSpaceChar(b))){
			buf[p++] = (char)b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	private static char[][] nm(int n, int m)
	{
		char[][] map = new char[n][];
		for(int i = 0;i < n;i++)map[i] = ns(m);
		return map;
	}

	private static int[] na(int n)
	{
		int[] a = new int[n];
		for(int i = 0;i < n;i++)a[i] = ni();
		return a;
	}

	private static int ni()
	{
		int num = 0, b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static long nl()
	{
		long num = 0;
		int b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static void tr(Object... o) { if(INPUT.length() != 0)System.out.println(Arrays.deepToString(o)); }
}

import java.util.*;
import java.util.Map.Entry;
import java.util.stream.Collectors;

class Main {
    public static void main(String[] args) throws Exception {
        final Scanner sc = new Scanner(System.in);
        long N;
        N = sc.nextLong();
        long[] D = new long[(int)(N)];
        for(int i = 0 ; i < N ; i++){
                D[i] = sc.nextLong();
        }
        solve(N, D);
    }

    static void solve(long N, long[] D){
        HashMap<Long, Long> G = new HashMap<Long, Long>();
        HashMap<Long, Long> V = new HashMap<Long, Long>();
        for(int i = 0 ; i < N ; i++) G.put((long)i+1, D[i]);

        G = G.entrySet().stream()
        .sorted(Entry.<Long,Long>comparingByValue().reversed())
        .collect(Collectors.toMap(Entry::getKey,Entry::getValue,(e1, e2)->e1,LinkedHashMap::new));
        List<Long> S = new ArrayList<>(G.values());
        List<Long> Key = new ArrayList<>(G.keySet());
        int n[] = new int[(int)N];
        Long T[] = new Long[(int)N-1];
        for(int i = 0 ; i < N ; i++){
            n[i] = 1;
        }
        for(int i = 0 ; i < N-1 ; i++){
            T[i] = S.get(i)-(long)N+2*(long)n[i];
            int flag = 0;
            for(int j = i+1 ; j < N ; j++){
                if(S.get(j).equals(T[i])){
                    n[j] += n[i];
                    V.put(Key.get(i),Key.get(j));
                    flag = 1;
                    break;
                }
            }
            if(flag == 0){
                System.out.println("-1");
                System.exit(0);
            }
        }
        for (Long key : V.keySet()) {
            System.out.println(key + " " + V.get(key));
        }

    }
}

