void main() {
	int n;
	scanf("%d", &n);
	int[] d = new int[n];
	foreach (i; 0..n) {
		scanf("%d", &d[i]);
	}
	alias Tuple!(int,int) P;
	P[] raw_p = new P[n];
	foreach (i; 0..n) {
		raw_p[i][0] = d[i];
		raw_p[i][1] = i;
	}
	auto p = raw_p.sort;
	int[] sz = new int[n];
	P[] ans;
	foreach_reverse (i; 1..n) {
		int v = p[i][1];
		sz[v]++;
		int diff = (n-sz[v]) - sz[v];
		int pi = p.lowerBound(P(d[v]-diff,-1)).length.to!int;
		if (pi == n || p[pi][0] != d[v]-diff) {
			writeln(-1);
			return;
		}
		int par = p[pi][1];
		sz[par] += sz[v];
		ans ~= P(v+1, par+1);
	}
	foreach (edge; ans) {
		writeln(edge[0], ' ', edge[1]);
	}
}

