use std::collections::BTreeMap;
use std::io::*;
use std::str::FromStr;

#[allow(dead_code)]
fn read<T: FromStr>() -> T {
    let stdin = stdin();
    let stdin = stdin.lock();
    let token: String = stdin
        .bytes()
        .map(|c| c.expect("failed to read char") as char)
        .skip_while(|c| c.is_whitespace())
        .take_while(|c| !c.is_whitespace())
        .collect();
    token.parse().ok().expect("failed to parse token")
}

fn dfs(from: usize, depth: i64, childs: &Vec<Vec<usize>>) -> i64 {
    let mut ret = depth;
    for &to in childs[from].iter() {
        ret += dfs(to, depth + 1, childs);
    }
    return ret;
}

fn main() {
    let n = read::<usize>();
    let mut items = BTreeMap::<i64, usize>::new();
    for i in 0..n {
        let v = read::<i64>();
        items.insert(v, i);
    }
    let mut childs = vec![vec![]; n];
    let mut parents = vec![n; n];
    let mut cnts = vec![1; n];
    for (&v, &index) in items.iter().rev() {
        let tv = v - (n as i64 - 2 * cnts[index]);
        if cnts[index] == n as i64 || tv <= 0 || tv == v {
            break;
        }
        if let Some(&nindex) = items.get(&tv) {
            parents[index] = nindex;
            childs[nindex].push(index);
            cnts[nindex] += cnts[index];
        } else {
            println!("-1");
            return;
        }
    }
    let (&root_weight, &root) = items.iter().next().unwrap();
    let weight = dfs(root, 0, &childs);
    if weight != root_weight {
        println!("-1");
        return;
    }
    for i in 0..n {
        if parents[i] == n {
            continue;
        }
        println!("{} {}", i + 1, parents[i] + 1);
    }
}

