fn err() -> ! {
    println!("{}", -1);
    std::process::exit(0);
}

fn main() {
    input!(n: usize, d: [u64; n]);
    let mut di: Vec<_> = d.iter().enumerate().map(|(i, &d)| (d, i)).collect();
    di.sort_by_key(|&p| std::cmp::Reverse(p));
    eprintln!("{:?}", di);

    let mut size = vec![1; n];
    let mut ans = vec![];
    for (si, &(d, i)) in di.iter().take(n - 1).enumerate() {
        let e = d - n as u64 + 2 * size[i];
        if d < e {
            err();
        }
        let (j, sj) = {
            let mut l = 0;
            let mut r = n;
            while 1 < r - l {
                let c = (l + r) / 2;
                if di[c].0 < e {
                    r = c;
                } else {
                    l = c;
                }
            }
            (di[l].1, l)
        };
        if sj <= si || di[sj].0 != e {
            err();
        }
        ans.push((j, i));
        size[j] += size[i];
        eprintln!("{:?}", &size);
    }
    for (u, v) in ans {
        println!("{} {}", u + 1, v + 1);
    }
}

