fn run() {
    input! {
        n: usize,
        distance_sum: [i64; n],
    }
    let mut d = distance_sum.iter().cloned().enumerate().collect::<Vec<_>>();
    d.sort_by_key(|d| d.1);
    let mut size = vec![1i64; n];
    let mut edge = Vec::with_capacity(n - 1);
    let mut deg = vec![0; n + 1];
    for i in (1..n).rev() {
        let (a, x) = d[i];
        let y = x - n as i64 + 2 * size[a];
        if let Ok(k) = d.binary_search_by(|d| d.1.cmp(&y)) {
            let b = d[k].0;
            size[b] += size[a];
            edge.push((a, b));
            deg[a] += 1;
            deg[b] += 1;
        } else {
            println!("-1");
            return;
        }
        size[a] = 0;
    }
    if !size.iter().any(|s| *s == n as i64) {
        println!("-1");
        return;
    }
    for i in 1..(n + 1) {
        deg[i] += deg[i - 1];
    }
    let mut buf = vec![0; 2 * (n - 1)];
    for &(a, b) in edge.iter() {
        deg[a] -= 1;
        buf[deg[a]] = b;
        deg[b] -= 1;
        buf[deg[b]] = a;
    }
    let neighbor = |v: usize| &buf[deg[v]..deg[v + 1]];
    let mut q = Vec::with_capacity(n);
    let mut parent = vec![0; n];
    let mut dfs = vec![0];
    while let Some(v) = dfs.pop() {
        q.push(v);
        let p = parent[v];
        for &u in neighbor(v).iter() {
            if u != p {
                parent[u] = v;
                dfs.push(u);
            }
        }
    }
    let mut dp1 = vec![0; n];
    size.clear();
    size.resize(n, 1);
    for &v in q.iter().rev() {
        let p = parent[v];
        for &u in neighbor(v).iter() {
            if u != p {
                dp1[v] += dp1[u] + size[u];
                size[v] += size[u];
            }
        }
    }
    let mut dp2 = dp1.clone();
    for &v in q.iter() {
        let p = parent[v];
        for &u in neighbor(v).iter() {
            if u != p {
                dp2[u] = dp2[v] + n as i64 - size[u] * 2;
            }
        }
    }
    if dp2.iter().zip(distance_sum.iter()).all(|p| *p.0 == *p.1) {
        let mut out = String::new();
        for (a, b) in edge {
            out.push_str(&format!("{} {}\n", a + 1, b + 1));
        }
        print!("{}", out);
    } else {
        println!("-1");
    }
}

fn main() {
    run();
}

