fn dfs(v: usize, par: Option<usize>, d: i64, g: &[Vec<usize>]) -> (i64, i64) {
    let mut tot = 0;
    let mut ssz = 1;
    for &w in g[v].iter() {
        if Some(w) == par { continue; }
        let (sub, sz) = dfs(w, Some(v), d + 1, g);
        tot += sub + sz;
        ssz += sz;
    }
    (tot, ssz)
}

fn solve() {
    let out = std::io::stdout();
    let mut out = BufWriter::new(out.lock());
    macro_rules! puts {
        ($format:expr) => (write!(out,$format).unwrap());
        ($format:expr, $($args:expr),+) => (write!(out,$format,$($args),*).unwrap())
    }
    input!{
        n: usize,
        d: [i64; n],
    }
    let mut pool = vec![];
    for i in 0 .. n {
        pool.push((d[i], i));
    }
    pool.sort();
    pool.reverse();
    let mut edges = vec![];
    let mut gad: HashMap<i64, Vec<(usize, i64)>> = HashMap::new();
    for (p, idx) in pool {
        if let Some(vs) = gad.remove(&p) {
            let mut sz = 0;
            for &(v, s) in vs.iter() {
                sz += s;
                edges.push((v, idx));
            }
            sz += 1;
            let targ = p + 2 * sz - n as i64;
            if sz != n as i64 {
                gad.entry(targ).or_insert(vec![]).push((idx, sz));
            }
        } else {
            let sz = 1;
            let targ = p + 2 * sz - n as i64;
            gad.entry(targ).or_insert(vec![]).push((idx, sz));
        }
    }
    let mut g = vec![Vec::new(); n];
    for &(u, v) in &edges {
        g[u].push(v);
        g[v].push(u);
    }
    let (d0, _) = dfs(0, None, 0, &g);
    if gad.len() == 0 && d[0] == d0 {
        for (u, v) in edges {
            puts!("{} {}\n", u + 1, v + 1);
        }
    } else {
        puts!("-1\n");
        return;
    }
}

fn main() {
    // In order to avoid potential stack overflow, spawn a new thread.
    let stack_size = 104_857_600; // 100 MB
    let thd = std::thread::Builder::new().stack_size(stack_size);
    thd.spawn(|| solve()).unwrap().join().unwrap();
}

