    let mut g = vec![vec![]; n];
    let mut ans = vec![];
    let mut size = vec![1; n];
    for (si, &i) in ord.iter().enumerate().take(n - 1) {
        if n as u64 <= size[i] * 2 {
            err();
        }
        let di = d[i];
        let dj = di - n as u64 + 2 * size[i];
        let sj = d_sorted_reverse
            .binary_search_by_key(&std::cmp::Reverse(dj), |&x| std::cmp::Reverse(x))
            .unwrap_or_else(|_| err());
        assert!(si < sj);
        let j = ord[sj];
        size[j] += size[i];
        ans.push((i, j));
        g[j].push(i);
    }

    let root = ord[n - 1];
    fn dfs(x: usize, g: &Vec<Vec<usize>>, size: &Vec<u64>) -> u64 {
        dbg!(x);
        let mut ans = 0;
        for &y in &g[x] {
            ans += dfs(y, &g, &size) + size[y];
        }
        ans
    }
    if dbg!(dfs(root, &g, &size)) != dbg!(d[root]) {
        err();
    }
    for (u, v) in ans {
        println!("{} {}", u + 1, v + 1);
    }
}

use std::io::{self, BufRead};
use std::collections::BTreeMap;

#[derive(Debug, Clone)]
struct Tree {
  id: usize,
  count: usize,
  children: Vec<Box<Tree>>,
}

fn dfs(t: &Box<Tree>, es: &mut Vec<(usize, usize)>) {
  for c in &t.children {
    es.push((t.id, c.id));
    dfs(&c, es);
  }
}

fn main() {
  let stdin = io::stdin();
  let mut lines = stdin.lock().lines();
  let n: usize = lines.next().unwrap().unwrap().parse().unwrap();
  let mut ds: Vec<(usize, usize)> = Vec::new();
  for i in 0..n {
    let x: usize = lines.next().unwrap().unwrap().parse().unwrap();
    ds.push((i + 1, x));
  }
  ds.sort_by(|x, y| y.1.cmp(&x.1));
  let mut forest: BTreeMap<usize, Vec<Box<Tree>>> = BTreeMap::new();
  for tup in &ds {
    let i = tup.0;
    let d = tup.1;
    let v = if let Some(cn) = forest.get(&d) {
      Tree { id: i, count: 1 + cn.iter().map(|c| c.count).sum::<usize>(), children: cn.to_vec() }
    } else {
      Tree { id: i, count: 1, children: Vec::new() }
    };
    forest.remove(&d);
    let k = d + 2 * v.count - n;
    let o = forest.get_mut(&k);
    if let Some(t) = o {
      t.push(Box::new(v));
    } else {
      forest.insert(k, vec![Box::new(v)]);
    }
  }
  if forest.len() != 1 || forest.iter().next().unwrap().1.len() != 1 {
    println!("-1");
    return;
  }
  let mut es: Vec<(usize, usize)> = Vec::new();
  let t = &forest.iter().next().unwrap().1[0];
  dfs(t, &mut es);
  for (x, y) in es {
    println!("{} {}", x, y);
  }
}

