fn main() {
    input! {
        n: usize,
        d: [i64; n]
    };
    let mut d = d.into_iter().zip(0..n).collect::<Vec<_>>();
    d.sort_by_key(|&(w,idx)| -w);

    let mut dmap = HashMap::new();
    for i in 0..n {
        dmap.insert(d[i].0, d[i].1);
    }

    let n64 = n as i64;
    let mut edges = vec![];
    let mut used = vec![false; n];
    let mut cnt = vec![1; n];

    let mut is_ok = true;
    for &(dist,vidx) in &d {
        let to_dist = dist - n64 + cnt[vidx] * 2;
        if to_dist > dist {
            continue;
        }
        // debug!(dist, to_dist, dmap.get(&to_dist));
        match dmap.get(&to_dist) {
            None => {
                is_ok = false;
                break;
            },
            Some(&idx) => {
                edges.push((vidx,idx));
                cnt[idx] += cnt[vidx];
            }
        }
    }

    is_ok &= edges.len() == n-1;

    d.sort_by_key(|&(w,idx)| idx);
    if is_ok {
        is_ok &= try_make(&d, &edges);
    }

    if is_ok {
        for (u, v) in edges {
            println!("{} {}", u+1, v+1);
        }
    } else {
        println!("-1");
    }


}

fn try_make(d: &Vec<(i64, usize)>, edges: &Vec<(usize, usize)>) -> bool {
    let n = edges.len()+1;
    let mut graph = vec![vec![]; n];
    for &(u,v) in edges {
        graph[u].push(v);
        graph[v].push(u);
    }
    let (nn, d0) = dfs(0, None, &graph);
    d[0].0 == d0
}

fn dfs(now: usize, par: Option<usize>, graph: &Vec<Vec<usize>>) -> (i64, i64) {
    let mut w = 1;
    let mut total_d = 0;
    for &to in &graph[now] {
        if Some(to) == par {
            continue;
        }
        let (wc, t) = dfs(to, Some(now), graph);
        w += wc;
        total_d += t + wc;
    }
    (w, total_d)
}

