fn run() {
    input! {
        n: usize,
        distance_sum: [i64; n],
    }
    let mut d = distance_sum.iter().cloned().enumerate().collect::<Vec<_>>();
    d.sort_by_key(|d| d.1);
    let mut size = vec![1i64; n];
    let mut edge = vec![];
    for i in (1..n).rev() {
        let (a, x) = d[i];
        let y = x - n as i64 + 2 * size[a];
        if let Ok(k) = d.binary_search_by(|d| d.1.cmp(&y)) {
            let b = d[k].0;
            size[b] += size[a];
            edge.push((a, b));
        } else {
            println!("-1");
            return;
        }
        size[a] = 0;
    }
    if !size.iter().any(|s| *s == n as i64) {
        println!("-1");
        return;
    }
    let mut g = vec![vec![]; n];
    for &(a, b) in edge.iter() {
        g[a].push(b);
        g[b].push(a);
    }
    let mut q = vec![];
    let mut dfs = vec![(0, 0)];
    while let Some((v, p)) = dfs.pop() {
        q.push(v);
        if let Some(k) = g[v].iter().position(|u| *u == p) {
            g[v].remove(k);
        }
        for &u in g[v].iter() {
            dfs.push((u, v));
        }
    }
    let mut dp1 = vec![0; n];
    let mut size = vec![1i64; n];
    for &v in q.iter().rev() {
        for &u in g[v].iter() {
            dp1[v] += dp1[u] + size[u];
            size[v] += size[u];
        }
    }
    let mut dp2 = dp1.clone();
    for &v in q.iter() {
        for &u in g[v].iter() {
            dp2[u] = dp2[v] + n as i64 - size[u] * 2;
        }
    }
    if dp2.iter().zip(distance_sum.iter()).all(|p| *p.0 == *p.1) {
        let mut out = String::new();
        for (a, b) in edge {
            out.push_str(&format!("{} {}\n", a + 1, b + 1));
        }
        print!("{}", out);
    } else {
        println!("-1");
    }
}

fn main() {
    run();
}

