use std::collections::BTreeMap;

fn check(es: &[(usize, usize)], n: usize) -> bool {
    if es.len() != n-1 {
        return false;
    }
    let mut sets = VecUnionFindSets::with_items(n);
    for &(a, b) in es {
        sets.unite(a, b).unwrap();
    }
    sets.count() == 1
}

fn main() {
    read!(n = u64);
    let mut d_to_v = BTreeMap::new();
    for (v, d) in read_lines::<u64>().enumerate() {
        d_to_v.insert(d, v);
    }
    let mut vs = d_to_v.clone();
    let mut es = Vec::new();
    while let Some((&(mut d), &(mut v))) = vs.iter().next_back() {
        vs.remove(&d);
        for w in iterate(n, |n| n.saturating_sub(2)).skip(1) {
            if w == 0 || d < w {
                break;
            }
            match d_to_v.get(&(d - w)) {
                None => break,
                Some(&v_next) => {
                    d = d - w;
                    vs.remove(&d);
                    es.push(minmax(v, v_next));
                    v = v_next;
                }
            }
        }
    }
    let ans = check(&es, n as usize).then_with(|| {
        es.sort();
        es.into_iter().map(|(a, b)| format!("{} {}", a+1, b+1)).join('\n')
    }).to_string_or(-1);
    println!("{}", ans);
}

use std::io::{self, BufRead};
use std::collections::BTreeMap;

#[derive(Debug, Clone)]
struct Tree {
  id: usize,
  count: usize,
  children: Vec<Box<Tree>>,
}

fn dfs(t: &Box<Tree>, es: &mut Vec<(usize, usize)>) {
  for c in &t.children {
    es.push((t.id, c.id));
    dfs(&c, es);
  }
}

fn main() {
  let stdin = io::stdin();
  let mut lines = stdin.lock().lines();
  let n: usize = lines.next().unwrap().unwrap().parse().unwrap();
  let mut ds: Vec<(usize, usize)> = Vec::new();
  for i in 0..n {
    let x: usize = lines.next().unwrap().unwrap().parse().unwrap();
    ds.push((i + 1, x));
  }
  ds.sort_by(|x, y| y.1.cmp(&x.1));
  let mut forest: BTreeMap<usize, Vec<Box<Tree>>> = BTreeMap::new();
  for tup in &ds {
    let i = tup.0;
    let d = tup.1;
    let contains = forest.contains_key(&d);
    let v = if contains {
      let cn = forest.remove(&d).unwrap();
      Tree { id: i, count: 1 + cn.iter().map(|c| c.count).sum::<usize>(), children: cn }
    } else {
      Tree { id: i, count: 1, children: Vec::new() }
    };
    let k = d + 2 * v.count - n;
    let contains = forest.contains_key(&k);
    if contains {
      if let Some(t) = forest.get_mut(&k) {
        t.push(Box::new(v));
      }
    } else {
      forest.insert(k, vec![Box::new(v)]);
    }
  }
  if forest.len() != 1 || forest.iter().next().unwrap().1.len() != 1 {
    println!("-1");
    return;
  }
  let mut es: Vec<(usize, usize)> = Vec::new();
  let t = &forest.iter().next().unwrap().1[0];
  dfs(t, &mut es);
  for (x, y) in es {
    println!("{} {}", x, y);
  }
}
