int n;
int a[N];

bool rev(int p) {
	if (a[p] != n) exit(1);
	if (a[p] > a[p + 1] && a[p + 1] > a[p + 2]) {
		swap(a[p + 2], a[p]);
		return true;
	} else return false;
}

bool work() {
	int p = 0;
	for (int i = n; i; --i)
		if (a[i] == n) { p = i; break; }
	if (!p) exit(0);
	if (p == n) return --n, true;
	if ((n - p) & 1) return false;
	for (int i = p; a[n] != n; i += 2) if (!rev(i)) return false;
	while (a[n] == n) --n;
	return n = p, true;
}

int main() {
	ios::sync_with_stdio(false);
	cin >> n;
	for (int i = 1; i <= n; ++i) cin >> a[i];
	while (n) if (!work()) return puts("No"), 0;
	puts("Yes");
	return 0;
}

