		sort(all(v));
		// cout << mn << " " << mx << "\n";
		if (v.front() != i + 1 || v.back() != j) {
			ok = false;
		}

    	j = i;
    }

    if (ok)
    	cout << "Yes\n";
    else
    	cout << "No\n";

    return 0;   
}

#include <iostream>
using namespace std;
int N;
int P[300005];
void Read()
{
    cin >> N;
    for(int i = 1; i <= N; i++)
        cin >> P[i];
}

void Solve()
{
    for(int i = 1; i <= N; i++)
    {
        if(P[i] == i)
        {
            continue;
        }
        int l = i, r = i;
        while(1)
        {
            if(r == N) break;
            if(r + 1 > N || P[r + 1] != r + 1)
                break;
            if(r + 2 > N || P[r + 2] == r + 2)
                break;
            r += 2;
        }
        int Min = N + 3, Max = 0;
        for(int j = l; j <= r; j += 2)
        {
            Min = min(Min, P[j]);
            Max = max(Max, P[j]);
        }
        if(Min < l || Max > r)
        {
            cout << "No\n";
            return;
        }
        int last = 0;
        for(int j = l; j <= r; j += 2)
            if(P[j] < j)
            {
                if(P[j] < last)
                {
                    cout << "No\n";
                    return;
                }
                last = P[j];
            }
        last = 0;
        for(int j = l; j <= r; j += 2)
            if(P[j] > j)
            {
                if(P[j] < last)
                {
                    cout << "No\n";
                    return;
                }
                last = P[j];
            }
        i = r;
    }
    cout << "Yes\n";
}
int main()
{
    Read();
    Solve();
    return 0;
}

#include <iostream>
