#include <vector>

using namespace std;

int main()
{
    int N;
    cin >> N;
    vector<int> p(N + 1);
    for (int i = 1; i <= N; i++)
    {
        cin >> p[i];
    }
    int pre_max = 0;
    int start = 0, end = 0;
    for (int i = 1; i <= N; i++)
    {
        if (i % 2 != p[i] % 2)
        {
            cout << "No" << endl;
            return 0;
        }
        pre_max = max(p[i], pre_max);
        if (pre_max == i)
        {
            end = i;
            if (end - start == 1)
            {
                continue;
            }
            else
            {
                for (int j = end - 1; j > start; j -= 2)
                {
                    if (p[j] != j)
                    {
                        cout << "No" << endl;
                        return 0;
                    }
                }

                int go_right_max = 0;
                int go_left_max = 0;
                int direction_flag = 0;
                for (int j = start + 1; j <= end; j += 2)
                {
                    if (j < p[j])
                    {
                        if (go_right_max > p[j] && direction_flag == 1)
                        {
                            cout << "No" << endl;
                            return 0;
                        }
                        go_left_max = 0;
                        go_right_max = p[j];
                        direction_flag = 1;
                    }
                    else if (j > p[j])
                    {
                        if ((go_left_max > p[j] && direction_flag == 2) || direction_flag == 0)
                        {
                            cout << "No" << endl;
                            return 0;
                        }
                        if (direction_flag == 1)
                        {
                            go_right_max = 0;
                            direction_flag = 2;
                        }
                        go_left_max = p[j];
                    }
                    else
                    {
                        if (direction_flag == 1)
                        {
                            cout << "No" << endl;
                            return 0;
                        }
                        direction_flag = 0;
                        go_left_max = 0;
                    }
                }
            }
            start = end;
        }
    }
    cout << "Yes" << endl;
    return 0;
}

