struct PII{
	int first,second;
	PII(){}
	PII(int f, int s): first(f),second(s){}
	inline bool operator <(const PII& x)const{
		if(first!=x.first) return first<x.first;
		return second<x.second;
	}
	inline bool operator ==(const PII& x)const{
		return first==x.first and second==x.second;
	}
	inline bool operator >(const PII& x)const{
		if(first!=x.first) return first>x.first;
		return second>x.second;
	}
	inline bool operator !=(const PII& x)const{
		return first!=x.first and second!=x.second;
	}
};
