#include<bits/stdc++.h>

using namespace std;

#define N 1600000
#define ls (x<<1)
#define rs (x<<1|1)

int n,a[N],seg[N],fst[N],mn[N],pos[N];

void build(int x,int l,int r){
	if (l==r){seg[x]=a[l]; return;}
	int mid=(l+r)>>1;
	build(ls,l,mid); build(rs,mid+1,r);
	seg[x]=max(seg[ls],seg[rs]);
}

int qry(int x,int l,int r,int L,int R){
	if (l>=L&&r<=R) return seg[x];
	int mid=(l+r)>>1,ret=0;
	if (L<=mid) ret=max(ret,qry(ls,l,mid,L,R));
	if (R>mid) ret=max(ret,qry(rs,mid+1,r,L,R));
	return ret;
}

int main(){
	scanf("%d",&n);
	for (int i=1;i<=n;++i){
		scanf("%d",a+i);
		if ((a[i]&1)!=(i&1)){puts("No"); return 0;}
		pos[a[i]]=i;
	}
	mn[n]=n+1; for (int i=n-1;i;--i) mn[i]=min(mn[i+1],a[i+1]);
	build(1,1,n);
	fst[n]=pos[n];
	for (int i=n-1;i;--i) fst[i]=min(fst[i+1],pos[i]);
	for (int i=1;i<=n;++i)
		if (fst[a[i]]+1<=i-1)
			if (qry(1,1,n,fst[a[i]]+1,i-1)>mn[i]){
				puts("No"); return 0;
			}
	puts("Yes");

	return 0;
}

