const int mod = 1e9 + 7;
inline int add(int x, int y) { x += y; return x >= mod ? x - mod : x; }
inline void inc(int &x, int y) { x += y; x -= x >= mod ? mod : 0; }

inline int Power(int x, int y) {
	int res = 1;
	while (y) {
		if (y & 1) res = (ll) res * x % mod;
		x = (ll) x * x % mod, y >>= 1;
	} return res;
}

template <class T> void chkmax(T &x, T y) { x = x > y ? x : y; }
template <class T> void chkmin(T &x, T y) { x = x < y ? x : y; }
template <class T> T gcd(T x, T y) { return !y ? x : gcd(x % y, y); }

int n;
int a[N], pos[N];

bool work(int &n) {
	int p = pos[n];
	if (p == n) return --n, true;
	if ((n - p) & 1) return false;
	for (int i = p + 1; i <= n; i += 2)
		if (a[i] < a[i + 1]) return false;
	for (int i = p; i < n; ++i) a[i] = a[i + 1];
	for (int i = p; i < n; i += 2) swap(a[i], a[i + 1]);
	for (int i = p + 1; i < n; ++i) if (a[i] != i) return false;
	for (int i = p; i < n; ++i) pos[a[i]] = i;
	return n = p, true;
}

