const int MAXN=300005;

int n,A[MAXN],type[MAXN];
bool Check()
{
	int last=1;
	for(int i=2;i<=n+2;i+=2)
		if(A[i]!=i)
		{
			for(int j=last;j<i&&j<=n;j+=2)
				if(A[j]==j)
					return false;
			last=i;
			i++;
			if(A[i]!=i)
				return false;
		}
	for(int i=1;i<=n;i++)
	{
		if(A[i]==i)
			type[i]=0;
		else
			type[i]=A[i]>i?-1:1;
	}
	last=0;
	for(int i=1;i<=n;i++)
		if(type[i]==-1)
		{
			if(A[i]<A[last])
				return false;
			last=i;
		}
	last=0;
	for(int i=1;i<=n;i++)
		if(type[i]==1)
		{
			if(A[i]<A[last])
				return false;
			last=i;
		}
	return true;
}

