struct segt
{
	int mind[1048576], maxd[1048576];

	void init()
	{
		memset(mind, INF, sizeof(mind));
		memset(maxd, 0, sizeof(maxd));
	}

	void modify(int pos, int dat)
	{
		pos += 524288;
		mind[pos] = maxd[pos] = dat;
		while(pos > 1) {
			pos >>= 1;
			mind[pos] = min(mind[pos << 1], mind[pos << 1 | 1]);
			maxd[pos] = max(maxd[pos << 1], maxd[pos << 1 | 1]);
		}
	}

	int qmind(int l, int r)
	{
		l += 524288; r += 524288;
		int ret = INF;
		while(l < r) {
			if(l & 1) ret = min(ret, mind[l]);
			if(!(r & 1)) ret = min(ret, mind[r]);
			l = l + 1 >> 1;
			r = r - 1 >> 1;
		}
		if(l == r) ret = min(ret, mind[l]);
		return ret;
	}

	int qmaxd(int l, int r)
	{
		l += 524288; r += 524288;
		int ret = 0;
		while(l < r) {
			if(l & 1) ret = max(ret, maxd[l]);
			if(!(r & 1)) ret = max(ret, maxd[r]);
			l = l + 1 >> 1;
			r = r - 1 >> 1;
		}
		if(l == r) ret = max(ret, maxd[l]);
		return ret;
	}
}tre1, tre2;

